import React, { Component } from 'react';
import styled from 'styled-components';
import { Radio, Select } from "antd";
import tsCheckIcon from '../../assets/ts_check.svg';
import ContentTitle from './ContentTitle';

const { Option } = Select;

class Contentcommon extends Component {

  state = {
    value: 1,
  };
  onChange = e => {
    console.log('radio checked', e.target.value);
    this.setState({
      value: e.target.value,
    });
  };

  constructor(props) {
    super(props);
    this.state = {
      lists: ["한국어", "English"]
    };

  }
  handleChange = event => {
    this.setState({ value: event.target.value });
  };



  render() {
    return (
      <Wrapper>
        <ContentTitle title="테마" subTitle="개인이 선호하는 컬러의 테마를 설정할 수 있습니다."></ContentTitle>
        <Radio.Group onChange={this.onChange} value={this.state.value}>
          <ThemeList>
            <ThemeItem>
              <Radio value={1}>기본 테마</Radio>
              <ThemeImage className={"checked"}>
                <ThemeThumb src="data:image/png;base64," />
              </ThemeImage>
            </ThemeItem>
            <ThemeItem>
              <Radio value={2}>테마 이름</Radio>
              <ThemeImage>
                <ThemeThumb src="data:image/png;base64," />
              </ThemeImage>
            </ThemeItem>
            <ThemeItem>
              <Radio value={3}>테마 이름</Radio>
              <ThemeImage>
                <ThemeThumb src="data:image/png;base64," />
              </ThemeImage>
            </ThemeItem>
          </ThemeList>
        </Radio.Group>
        <ContentTitle title="언어" subTitle="언어 설정에 따라 날짜 및 시간 형식이 결정됩니다."></ContentTitle>
        <Select dropdownRender={menu => <StyledMenu>{menu}</StyledMenu>} defaultValue={this.state.lists[0]}>
          {this.state.lists.map(list => (
            <Option key={list} value={list}>{list}
            </Option>
          ))}
        </Select>
      </Wrapper>
    );
  }
}

const ThemeList = styled.ul`
  display: flex;
  margin: 1.19rem 0 2.065rem;
`
const ThemeItem = styled.li`
  display: flex;
  flex-direction: column-reverse;
  margin-right: 3.31rem;
  &:last-child {
    margin-right: 0;
  }
  .ant-radio-wrapper {
    font-size: 0.75rem;
    line-height: 0.94rem;
    color: #000;
    .ant-radio {
      input {
        width: 100%;
        height: 100%;
      }
      &-checked {
        .ant-radio-inner {
          border: none;
        }
      }
    }
    &-checked {
      + div {
        background-color: red;
        &:after {
          border: 2px solid #6C56E5;
        }
      }
    }
    .ant-radio-inner {
      position: relative;
      width: 0.94rem;
      height: 0.94rem;
      -webkit-transition: none;
      transition: none;
      &:after {
        content: '';
        display: inline-block;
        position: absolute;    
        top: 0.25rem;
        left: 0.25rem;
        width: 0.44rem;
        height: 0.44rem;
        border-radius: 50%;
        -webkit-transition: none;
        transition: none;
      }
    }
  }
`
const ThemeImage = styled.div`
  overflow: hidden;
  position: relative;
  margin-bottom: 0.63rem;
  border-radius: 0.63rem;
  :after {
    content: '';
    position: absolute;
    left: 0;
    right: 0;
    top: 0;
    bottom: 0;
    border-radius: 0.63rem;
    border: 1px solid #E3E7EB;
  }
  &.checked:after {
  }
`
const ThemeThumb = styled.img`
  width: 100%;
`

const Wrapper = styled.div`
  .ant-select {
    margin-top: 1.19rem;
    min-width: 10rem;
    height: 1.88rem;
    &-open {
      .ant-select-arrow {
        transform: rotate(-180deg);
      }
    }
  }
`
const StyledMenu = styled.div`
  & .ant-select-item {
    display: flex;
    align-items: center;
    &:before {
      content: '';
      display: inline-block;
      width: 0.7rem;
      height: 0.7rem;
      margin-right: 0.31rem;
      background-repeat: no-repeat;
      background-size: 0.7rem 0.7rem;
    }
    &-option-selected:before {
      background-image: url(${tsCheckIcon});
    }
  }
`

export default Contentcommon;